import styled from 'styled-components'

export const Botao = styled.button`
    display: flex;
    flex-direction: column;
    background-color: #5c8d8c;
    font-size: 50px;
    text-transform: uppercase;   
    border: none;
    justify-content: center;
    align-items: center;
`
