import axios from 'axios'

const Principal = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`

export default class App extends Component {


  render() {

    const body = {
      name: "Wilson Castro",
      email: "wilson@teste.com"
    }

    const createUser = axios.post("https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users", {
      headers: {
